$_MODULE['<{gshopping}prestashop>faq_ec63ce96861969f541066bd10cf8c3f2'] = 'Brand * ';
$_MODULE['<{gshopping}prestashop>faq_4280aa2d764024abfe9c505cade13a91'] = 'GTIN (Code EAN 13 ou UPC ou USBN) *';
$_MODULE['<{gshopping}prestashop>faq_18f14c926ae3e6c7113deaebe5fdc00c'] = 'Si vous n’avez pas rempli ces attributs pour vos produits, veillez à le faire dans chacune des pages produit concernées avant d’enregistrer et d’exporter votre flux. ';
$_MODULE['<{gshopping}prestashop>faq_de09c56b31c3d356661287ae7089fab9'] = '*Les deux derniers attributs sont demandés uniquement pour certains pays (France, Allemagne, UK, USA et Japon)';
$_MODULE['<{gshopping}prestashop>faq_07469399b4ac737422afbb51ebc25723'] = 'Remarque : Les attributs obligatoires pour Google Shopping varient selon les pays et les catégories Google. Pour plus d’informations, voir ce';
$_MODULE['<{gshopping}prestashop>faq_6ddef4b81d9ebf95762e6e36bd9dbae1'] = 'lien.';
$_MODULE['<{gshopping}prestashop>faq_4066808e6c2228b3b311756d111fe97b'] = 'Comment configurer ce module ?';
