$_MODULE['<{gshopping}prestashop>faq_1d107a6ab7b92966c90205b591f2f513'] = 'Allemagne, Australie, Autriche, Belgique, Brésil, Canada, Danemark, Espagne, France, Inde, Italie, Japon, Mexique, Norvège, Pays-Bas, Pologne, Royaume-Uni, Russie, République tchèque, Suisse, Suède, Turquie et États-Unis, entre autres. ';
$_MODULE['<{gshopping}prestashop>faq_4b3e22285f7d6035b802c93e83f6e50d'] = 'J\'ai eu un message d\'erreur concernant les micro data. Qu\'est-ce que cela signifie ? ';
$_MODULE['<{gshopping}prestashop>faq_d15474d10cd0a4903ab3991dcc7c44b0'] = 'Vous utilisez une option incorrecte pour envoyer les données à Google. Ce module ne fonctionne pas avec les micro data, vous devez utiliser l\'exportation par flux.';
